import { ITextFieldProps } from '@fluentui/react/lib/TextField';
export interface INumericalFieldProps {
    /** Properties for the underlying TextField control. */
    textFieldProps?: ITextFieldProps;
    /**
     * The numerical value of the field.
     *
     * The controlled value is expected to be a valid numerical value for
     * the particular text field. Giving an incorrect value
     * will result in unexpected behavior.
     */
    value?: number;
    /**
     * Optionally, whether the number should be formatted (i.e., comma separation).
     * Defaults to true.
     */
    formatValue?: boolean;
    /**
     * A callback for when the value of the numerical field has changed.
     * Note that it is only called when the TextField has lost focus,
     * rather than as the user is actively typing.
     */
    onBlurred?: (newValue: number | undefined) => void;
}
