import * as React from 'react';
export interface IExpandIconProps {
    /** Whether the expand icon actually has an expand icon. Will render a placeholder instead, if not. */
    isFolder: boolean;
    /** Whether to disable the collapse/expand button.  */
    disable: boolean;
    /**
     * The Fabric icon to use for the collapsed state of the collapse/expand button.
     * This icon will be rotated clockwise 90 degrees when in the expanded state.
     */
    iconName: string;
    /** The class name to apply to the expand/collapse button. */
    buttonClassName: string;
    /** The class Name to apply to the icon inside the expand/collapse button. */
    iconClassName: string;
    /** The class name to apply to the place holder when not a folder. */
    noExpandClassName: string;
    /** The call back to call when the expand/collapse button is clicked. */
    onClick: (e: React.MouseEvent<HTMLElement>) => void;
}
