import { warn } from '@fluentui/react/lib/Utilities';

export const printDeprecationWarning = (
  componentName: string,
  fabricFallbackComponent?: string,
  customFallbackMessage?: string
) => {
  warn(getDeprecationWarningMessage(componentName, fabricFallbackComponent, customFallbackMessage));
};

export const getDeprecationWarningMessage = (
  componentName: string,
  fabricFallbackComponent?: string,
  customFallbackMessage?: string
) => {
  const fallbackMessage =
    customFallbackMessage ||
    (fabricFallbackComponent
      ? `Please use the ${fabricFallbackComponent} component from Office UI Fabric instead.`
      : 'Please avoid using this component.');

  return `Warning: The ${componentName} component will soon be deprecated in a future release.\n${fallbackMessage}`;
};
