import { SiteMessageBar } from '@fluentui/react-docsite-components/lib/components/SiteMessageBar';
import * as React from 'react';
import { getDeprecationWarningMessage } from './warn';

interface WarningMessageBarProps {
  componentName: string;
  fabricFallbackComponent?: string;
  customFallbackMessage?: string;
}

export const WarningMessageBar: React.FunctionComponent<WarningMessageBarProps> = (
  props: WarningMessageBarProps
) => {
  const { componentName, fabricFallbackComponent, customFallbackMessage } = props;
  return (
    <SiteMessageBar
      text={getDeprecationWarningMessage(componentName, fabricFallbackComponent, customFallbackMessage)}
      styles={{
        root: {
          backgroundColor: `rgba(232, 17, 35, 0.2)`
        }
      }}
    />
  );
};
