import * as React from 'react';

import { classNamesFunction } from '@fluentui/react/lib/Utilities';

import { CardProps, CardStyleProps, CardStyles } from './Card.types';

const getClassNames = classNamesFunction<CardStyleProps, CardStyles>();

export class CardBase extends React.Component<CardProps> {
  private cardRef: React.RefObject<HTMLDivElement>;

  constructor(props: CardProps) {
    super(props);

    this.cardRef = React.createRef<HTMLDivElement>();
  }
  public render() {
    const { children, className, width, height, renderContentArea, styles, theme, onMouseLeave } = this.props;
    const classNames = getClassNames(styles, { width, height, theme, className });

    return (
      <div ref={this.cardRef} className={classNames.root} onMouseLeave={onMouseLeave}>
        {renderContentArea ? renderContentArea() : children}
      </div>
    );
  }

  public getRef = () => {
    return this.cardRef;
  };
}
