import { IStyleFunction } from '@fluentui/react/lib/Utilities';

import { CardStyleProps, CardStyles } from './Card.types';

export const styles: IStyleFunction<CardStyleProps, CardStyles> = ({ width, height, theme, className }) => {
  const { effects, palette } = theme;

  return {
    root: [
      'ba-Card',
      {
        height: height,
        maxHeight: height,
        width: width,
        maxWidth: width,
        display: 'flex',
        position: 'relative',
        flexDirection: 'column',
        border: `1px solid ${palette.neutralLight}`,
        borderRadius: '3px',
        boxSizing: 'border-box',
        boxShadow: effects.elevation4,
        transition: 'box-shadow 250ms ease-in-out',
        selectors: {
          ':hover': {
            boxShadow: effects.elevation16
          }
        }
      },
      className
    ]
  };
};
