import * as React from 'react';

import { DefaultButton, PrimaryButton } from '@fluentui/react/lib/Button';
import { Link } from '@fluentui/react/lib/Link';
import { classNamesFunction } from '@fluentui/react/lib/Utilities';

import { CardFooterProps, CardFooterStyleProps, CardFooterStyles } from './CardFooter.types';

const getClassNames = classNamesFunction<CardFooterStyleProps, CardFooterStyles>();

export const CardFooterBase = (props: CardFooterProps) => {
  const { buttons, children, className, link, theme, styles } = props;
  const classNames = getClassNames(styles, {
    theme,
    className
  });

  return (
    <div className={classNames.root}>
      {children ? (
        children
      ) : (
        <>
          {link && (
            <Link className={classNames.link} {...link}>
              {link.text}
            </Link>
          )}
          {buttons && (
            <div className={classNames.buttonHolder}>
              <PrimaryButton {...buttons.primaryButton} />
              {buttons.secondaryButton && <DefaultButton {...buttons.secondaryButton} />}
            </div>
          )}
        </>
      )}
    </div>
  );
};
