import 'jest';

import * as Enzyme from 'enzyme';
import * as React from 'react';

import { PrimaryButton } from '@fluentui/react/lib/Button';
import { Link } from '@fluentui/react/lib/Link';

import { CardFooter } from './';

describe('CardFooterBase', () => {
  it('Basic rendering of the Card Footer', () => {
    const wrapper = Enzyme.mount(<CardFooter />);

    // should just render, w/o issue and no content
    const findPrimaryButton = wrapper.find(PrimaryButton);
    expect(findPrimaryButton.length).toBe(0);
    const findLink = wrapper.find(Link);
    expect(findLink.length).toBe(0);

    wrapper.unmount();
  });

  it('Render with Link and PrimaryButton', () => {
    const wrapper = Enzyme.mount(
      <CardFooter link={{ text: 'Hyperlink' }} buttons={{ primaryButton: { text: 'Button' } }} />
    );

    const findPrimaryButton = wrapper.find(PrimaryButton);
    expect(findPrimaryButton.length).toBe(1);
    const findLink = wrapper.find(Link);
    expect(findLink.length).toBe(1);

    wrapper.unmount();
  });
});
