import { IStyleFunction } from '@fluentui/react/lib/Utilities';

import { RichCardStyleProps, RichCardStyles } from './RichCard.types';

export const styles: IStyleFunction<RichCardStyleProps, RichCardStyles> = ({
  theme,
  className,
  hasHeader
}) => {
  const { palette } = theme;

  return {
    root: ['ba-RichCard', {}, className],
    contentArea: [
      {
        paddingBlockStart: hasHeader ? 12 : 16,
        paddingBlockEnd: 16,
        paddingInlineStart: 16, // LTR - Left
        paddingInlineEnd: 16 // LTR - Right
      }
    ],
    footer: [
      {
        borderTop: `1px solid ${palette.neutralLight}`
      }
    ]
  };
};
