import { ILinkProps, Link } from '@fluentui/react/lib/Link';
import * as React from 'react';

import { EmptyState } from '..';

const doNothing = { onClick: () => undefined };
const primaryMessage = 'This is primary messaging';
const firstSecondary =
  'This is secondary messaging where a description with more information is placed. This section can also include a link.';
const customSecondary = 'The link can have its own custom render function.';
const secondSecondary = 'More information can be placed below in this space if needed.';
const callToAction = 'Call to action';
const customStyle = {
  color: 'red',
  marginLeft: '5px'
};
const customLinkRender = (props: ILinkProps) => {
  return (
    <a style={customStyle}>
      Custom
      <Link {...props}>Link</Link>
    </a>
  );
};

export class EmptyStateBasicExample extends React.Component<{}> {
  constructor(props: {}) {
    super(props);
  }

  public render(): JSX.Element {
    return (
      <div style={{ display: 'flex', width: '100%', justifyContent: 'space-around' }}>
        <EmptyState
          icon={'Database'}
          primaryMessage={primaryMessage}
          callToActionText={callToAction}
          callToActionProps={doNothing}
          maxWidth={200}
        />
        <EmptyState
          icon={'Clear'}
          primaryMessage={primaryMessage}
          secondaryMessage={firstSecondary}
          maxWidth={200}
        />
        <EmptyState
          icon={'PowerAppsLogo'}
          primaryMessage={primaryMessage}
          secondaryMessage={firstSecondary}
          callToActionText={callToAction}
          callToActionProps={doNothing}
          maxWidth={200}
        />
        <EmptyState
          icon={'Globe'}
          primaryMessage={primaryMessage}
          secondaryMessage={[firstSecondary, secondSecondary]}
          callToActionText={callToAction}
          callToActionProps={doNothing}
          learnMoreLabel={'Learn more'}
          learnMoreProps={{ href: 'https://developer.microsoft.com/en-us/fluentui#/controls/web/link' }}
          maxWidth={300}
        />
        <EmptyState
          icon={'Globe'}
          primaryMessage={primaryMessage}
          secondaryMessage={customSecondary}
          callToActionText={callToAction}
          callToActionProps={doNothing}
          onRenderLink={customLinkRender}
          maxWidth={300}
        />
      </div>
    );
  }
}
