import * as React from 'react';
import { HighlightedText } from '../HighlightedText';

export class HighlightedTextBasicExample extends React.Component {
  public render(): JSX.Element {
    return (
      <div>
        <HighlightedText
          text={'Hello world. It is a marvelous time to be a highlight.'}
          highlights={[
            {
              startIndex: 0,
              length: 11
            },
            {
              startIndex: 21,
              length: 9
            }
          ]}
          labels={{
            beginHighlightedText: 'Begin highlighted text',
            endHighlightedText: 'End highlighted text'
          }}
        />
      </div>
    );
  }
}
