import { IButtonProps } from '@fluentui/react/lib/Button';
import { ISpinnerProps } from '@fluentui/react/lib/Spinner';
import { IComponentAs, IRenderFunction } from '@fluentui/react/lib/Utilities';

export interface ILoadingButtonProps {
  /**
   * Whether to show the loading state.
   */
  readonly loading?: boolean;

  /**
   * A custom renderer for the loading spinner.
   */
  readonly spinnerAs?: IComponentAs<ISpinnerProps>;
}

/**
 * The `loadingButton` higher-order component supports wrapping any component that has a matching `onRenderIcon` property.
 */
export interface ISupportedLoadingButtonTarget {
  readonly onRenderIcon?: IRenderFunction<IButtonProps>;
}
