import * as React from 'react';
import { IntegerField } from '../';

export interface IIntegerFieldExampleState {
  readonly currentValue: number | undefined;
}

export class IntegerFieldExample extends React.Component<{}, IIntegerFieldExampleState> {
  constructor(props: {}) {
    super(props);

    this.state = {
      currentValue: 0
    };
  }

  public render(): JSX.Element {
    return (
      <div style={{ display: 'flex', flexDirection: 'column' }}>
        <IntegerField
          textFieldProps={{
            label: 'Integer example'
          }}
          value={this.state.currentValue}
          onBlurred={this.onChanged}
          formatValue={false}
        />
        <p>Last valid value: {this.state.currentValue}</p>
      </div>
    );
  }

  private onChanged = (newValue: number | undefined) => {
    this.setState({
      currentValue: newValue
    });
  };
}
