import { Toggle } from '@fluentui/react/lib/Toggle';
import * as React from 'react';

import { Pane } from '../Pane';

export interface IPaneState {
  showPane: boolean;
}

export class PaneExample extends React.Component<{}, IPaneState> {
  constructor(props: {}) {
    super(props);
    this.state = {
      showPane: false
    };
  }

  public render() {
    const { showPane } = this.state;

    return (
      <div>
        <Toggle label="Show pane" checked={showPane} onChanged={this.onChange} />
        <br />
        <br />
        <section
          style={{ border: '2px solid black', position: 'relative', display: 'flex', height: '400px' }}
        >
          <section style={{ height: '100%', width: '100%', overflow: 'auto' }}>
            <div style={{ height: '700px' }} />
          </section>
          <Pane
            isOpen={showPane}
            onDismiss={this._hidePane}
            hasCloseButton={true}
            closeButtonAriaLabel="Close"
            headerText="Pane"
          >
            <span>Content goes here.</span>
          </Pane>
        </section>
      </div>
    );
  }

  private onChange = (checked: boolean) => {
    this.setState({ showPane: checked });
  };

  private _hidePane = () => {
    this.setState({ showPane: false });
  };
}
