import { Stack } from '@fluentui/react/lib/Stack';
import * as React from 'react';
import { Presence } from '../Presence';
import { getMockPresentUsers, MockPresenceStrings } from '../PresenceMockData';

/**
 * Example of Presence component with showAccordion: true.
 */
export class PresenceAccordionExample extends React.Component {
  public render(): JSX.Element {
    return (
      <Stack maxWidth={400} gap={16}>
        <Presence presentUsers={getMockPresentUsers()} showAccordion={true} strings={MockPresenceStrings} />
      </Stack>
    );
  }
}
