import { ZIndexes } from '@fluentui/react/lib/Styling';
import { IStyleFunction } from '@fluentui/react/lib/Utilities';
import { ResizerStyleProps, ResizerStyles } from './Resizer.types';

export const getStyles: IStyleFunction<ResizerStyleProps, ResizerStyles> = (props: ResizerStyleProps) => {
  const { palette } = props.theme!;

  const activeResizer = {
    opacity: 1,
    backgroundColor: palette.neutralTertiary
  };

  return {
    root: {
      position: 'absolute',
      backgroundColor: palette.neutralSecondary,
      borderRadius: 0,
      transition: '0.5s opacity, 0.5s background-color',
      opacity: 0,
      zIndex: ZIndexes.Layer,
      selectors: {
        ':hover': {
          transition: '0.2s opacity, 0.2s background-color',
          opacity: 1
        },
        ':focus': activeResizer,
        ':active': activeResizer
      }
    }
  };
};
