import { mergeStyles } from '@fluentui/react/lib/Styling';
import { BaseComponent, divProperties, getNativeProps } from '@fluentui/react/lib/Utilities';
import * as React from 'react';
import { IToolboxItemProps } from './ToolboxItem.types';

export class ToolboxItem extends BaseComponent<IToolboxItemProps, {}> {
  public render(): JSX.Element {
    const className = mergeStyles(
      {
        minHeight: 0, // For Edge, Mozilla overflow property
        display: 'flex',
        flexGrow: 1,
        selectors: {
          // For IE 10+ overflow property with fieldheaderregion minheight
          '@media all and (-ms-high-contrast: none)': {
            minHeight: 'calc(100% - 48px)'
          }
        }
      },
      this.props.className
    );

    return (
      <div {...getNativeProps(this.props, divProperties)} className={className}>
        {this.props.children}
      </div>
    );
  }
}
