import { Icon } from '@fluentui/react/lib/Icon';
import * as React from 'react';
import { IItemIconProps } from './ItemIcon.types';

export class ItemIcon extends React.Component<IItemIconProps> {
  public render(): JSX.Element {
    const { iconProps, label, itemIconContainerStyle, itemIconStyle, noItemIconStyle } = this.props;

    if (iconProps) {
      return (
        <div className={itemIconContainerStyle} title={label}>
          <Icon {...iconProps} className={itemIconStyle} />
        </div>
      );
    }

    return <span className={noItemIconStyle} />;
  }
}
