import { IIconProps } from '@fluentui/react/lib/Icon';

export interface IItemIconProps {
  /** The tooltip label to apply to the icon. */
  label: string;

  /**
   * The iconProps to provide to the underlying Fabric Icon.
   * If no iconProps are provided, a placeholder will be rendered instead of an icon.
   */
  iconProps?: IIconProps;

  /** The style to apply to the container which houses the Fabric Icon. */
  itemIconContainerStyle: string;

  /** The style to apply to the underlying Fabric Icon. */
  itemIconStyle: string;

  /** The style to apply to placeholder when there is no icon.. */
  noItemIconStyle: string;
}
