import * as React from 'react';

import { INodeLevelIndentationProps } from './NodeLevelIndentation.types';

export class NodeLevelIndentation extends React.PureComponent<INodeLevelIndentationProps> {
  public render(): JSX.Element {
    const { level, className } = this.props;

    return (
      <>
        {this._createFillArray(level).map((_, index) => (
          <LeftSpacer key={index} className={className} />
        ))}
      </>
    );
  }

  private _createFillArray(fillCount: number): Array<{}> {
    return Array.apply(null, new Array(fillCount));
  }
}

const LeftSpacer = ({ className }: { className: string }): JSX.Element => <span className={className} />;
