import * as Enzyme from 'enzyme';
import 'jest';
import { getTheme } from '@fluentui/react/lib/Styling';
import * as React from 'react';
import { StepStatus } from '../StepStatus';
import { WizardProgressBase as WizardProgress } from './WizardProgress.base';
import { getStyles } from './WizardProgress.styles';

describe('Wizard Progress', () => {
  it('marks the current step', () => {
    const wrapper = Enzyme.shallow(
      <WizardProgress
        styles={getStyles}
        theme={getTheme()}
        stepNames={['Step1', 'Step2']}
        stepStatuses={[StepStatus.Valid, StepStatus.Empty]}
        stepSummaries={[['Done'], []]}
        currentIndex={0}
        computeAriaDescription={() => 'description'}
        expandButtonLabel="Expand timeline"
        onProgressStepClick={(step: number) => null}
        collapseButtonLabel="Collapse timeline"
      />
    );

    expect(wrapper.find('.ba-wizard-progress-current').length).toBe(1);
  });
});
