import { ActionButton } from '@fluentui/react/lib/Button';
import { Link } from '@fluentui/react/lib/Link';
import { classNamesFunction } from '@fluentui/react/lib/Utilities';
import * as React from 'react';
import { getStyles } from './TipsAndHelpCard.styles';
import {
  TipsAndHelpCardProps,
  TipsAndHelpCardStyleProps,
  TipsAndHelpCardStyles
} from './TipsAndHelpCard.types';

const getClassNames = classNamesFunction<TipsAndHelpCardStyleProps, TipsAndHelpCardStyles>();

export function TipsAndHelpCardBase(props: TipsAndHelpCardProps) {
  const { title, content, action, actionLink } = props.card;
  const classNames = getClassNames(getStyles, {
    theme: props.theme!,
    className: props.className
  });

  return (
    <div className={classNames.root}>
      <div className={classNames.content}>
        <h5 className={classNames.title}>{title}</h5>
        <div className={classNames.text}>{content}</div>
      </div>
      {(action || actionLink) && (
        <footer className={classNames.action}>
          {action && (
            <ActionButton
              iconProps={{ iconName: action.iconName }}
              onClick={action.onClick}
              className={classNames.button}
              disabled={action.isDisabled}
            >
              {action.name}
            </ActionButton>
          )}
          {actionLink &&
            (actionLink.iconName ? (
              <ActionButton
                iconProps={{ iconName: actionLink.iconName }}
                href={actionLink.href}
                target={actionLink.target || '_blank'}
                className={classNames.button}
                onClick={actionLink.onClick}
              >
                {actionLink.name}
              </ActionButton>
            ) : (
              <Link
                href={actionLink.href}
                target={actionLink.target || '_blank'}
                onClick={actionLink.onClick}
              >
                {actionLink.name}
              </Link>
            ))}
        </footer>
      )}
    </div>
  );
}
