/**
 * There are currently no BAF Specific icons, however, this
 * file is here to ensure future logic is in place to allow
 * for Team specific icons as well as BAF icons we might need
 */

import { initializeIcons } from '@fluentui/react/lib/Icons';
import { IIconOptions, registerIcons } from '@fluentui/react/lib/Styling';

export const BAFIcons: { [key: string]: string | JSX.Element } = {};

export const BAFIconOptions: Partial<IIconOptions> = {};

/**
 *
 * @param iconSubset: { [key: string]: string | JSX.Element }
 * @param iconOptions: Partial<IIconOptions>
 * @description Registers BusinessApp icons as well as team icons, and initializes all UI Fabric Icons.
 */
export const icons = (
  iconSubset?: { [key: string]: string | JSX.Element },
  iconOptions?: Partial<IIconOptions>
) => {
  registerIcons(
    iconSubset ? { icons: { ...BAFIcons, ...iconSubset } } : { icons: BAFIcons },
    iconOptions ? { ...BAFIconOptions, ...iconOptions } : BAFIconOptions
  );

  initializeIcons();
};
